import React, { Component } from "react";
import "./Signup.css";
import {withProvider} from "../GlobalProvider.js"

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            username: "", 
            password: "", 


        }
    }

    validateForm() {
        return this.state.password.length > 0 && this.state.username.length > 0;
    }

    handleChange = event => {
        this.setState({
          [event.target.id]: event.target.value
        });

    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(this.props);
        this.props.signup(this.state)
            .then(() => this.props.history.push("/"));

    }

    render() { 
        return ( 
            <div className="container">
                <div className="text"> Sign Up here </div>
                <form onSubmit={this.handleSubmit}>

                        <label>Username </label>
                        <input onChange={this.handleChange} type="text" className="form-control" placeholder="Enter Username" value={this.state.username} autoComplete="off"/>


                        <label htmlFor="password">Password</label>
                        <input type="text" onChange={this.handleChange} autoComplete="off" value={this.state.password} className="form-control" placeholder="Enter Password"/>

                        <input type="submit" value="Submit" />
                </form>
           </div>
        );
    }
}

export default withProvider(Signup);
