import React, { Component } from "react";
import "./Signup.css";
import {withProvider} from "../GlobalProvider.js"

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            username: "", 
            password: "", 

        }

    }

    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }

    handleChange = event => {
        this.setState({
          username: event.target.value
        });

    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(this.state.username);
        this.props.signup(this.state)
            .then(() => this.props.history.push("/"));

    }

    render() { 
        return ( 
            <div className="container">
                <div className="text"> Sign Up here </div>
                <form onSubmit={this.handleSubmit}>

                       Username 

                        <input value={this.state.username} name="username" type="text" placeholder="Enter Username" autoComplete="off" className="form-control" onChange={this.updateUser}/>      

                        <label htmlFor="password">Password</label>
                        <input type="text" onChange={this.handleChange} value={this.props.password} autoComplete="off" className="form-control" placeholder="Enter Password"/>

                        <button type="submit" className="btn btn-primary">Submit</button>
                </form>
           </div> 


        );
    }
}

export default withProvider(Signup);
